import React from 'react'
import {Button,Modal,Form}from 'react-bootstrap'
import {useState,useEffect} from 'react'
import axios from 'axios';



function Ubdat({el}) {
    // pour recupere la valeur
    const [Inputtt,setInputtt]=useState(
        {
            title:el.title,
            genres:el.genres,
            director:el.director,
            runtime:el.runtime,
            posterUrl:el.posterUrl
        }
    )
    const handleChangee=(e)=>{
        const {name,value}=e.target;
        setInputtt(
            {
                ...Inputtt,[name]:value

            }
        );
        console.log(Inputtt)
    }

    const updatRow=(id)=> {

        axios.put(`http://localhost:3008/posts/${id}`, Inputtt)
        .then(response => {
          setInputtt( response.data);})
      }
      console.log('updaaaaaate',  Inputtt)

      useEffect(()=> {updatRow()
      },[]   )


//fin recuperation    
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
    return (
        <div>
             <>
      <button onClick={handleShow} >    <i class="fas fa-edit"></i>  </button>


      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            { /*----------FormModal---------*/ }
            <Form >
                <Form.Group controlId="formGroupTexte">
                    <Form.Label>Title</Form.Label>
                    <Form.Control 
                      type="texte" 
                      defaultValue={el.title}
                      placeholder="enter texte" 
                      onChange={handleChangee} 
                      value={Inputtt.title} 
                      name="title"  
                    />
                </Form.Group>
                <Form.Group controlId="formGroupTexte">
                    <Form.Label>Geners</Form.Label>
                    <Form.Control 
                    type="texte" 
                    defaultValue={el.genres}  
                    placeholder="enter texte"  
                    onChange={handleChangee} value={Inputtt.genres} name="genres"  />
                </Form.Group>
                <Form.Group controlId="formGroupTexte">
                    <Form.Label>Director</Form.Label>
                    <Form.Control type="texte" defaultValue={el.director}  placeholder="enter texte" onChange={handleChangee} value={Inputtt.director} name="director" />
                </Form.Group>
                <Form.Group controlId="formGroupTexte">
                    <Form.Label>Runtime</Form.Label>
                    <Form.Control type="texte" defaultValue={el.runtime}  placeholder="enter texte" onChange={handleChangee} value={Inputtt.runtime} name="runtime"  />
                </Form.Group>
                <Form.Group controlId="formGroupTexte">
                    <Form.Label>PosteUrl</Form.Label>
                    <Form.Control type="texte" defaultValue={el.posterUrl}  placeholder="enter texte" onChange={handleChangee} value={Inputtt.posterUrl} name="posterUrl" />
                </Form.Group>
                 <Button variant="primary" type="submit" onClick={() =>{ updatRow(el.id)}}>
                   Submit
                 </Button>
                </Form>
            {/*---------------*/ }
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
        </div>
    )
}
export default Ubdat ;
