import React from 'react'
import { Component } from 'react';
import {Card} from 'react-bootstrap';
import axios from 'axios';
import Rater from 'react-rater'
import 'react-rater/lib/react-rater.css'

import {Button,Form,Tab,Row,Col,Nav} from 'react-bootstrap';

class Admine extends Component {
  state={
    users: []
  }
  componentDidMount() {
    axios.get('http://localhost:3007/posts')
    .then(res => {

      this.setState({
        users: res.data
      })

    })
   }
   render(){
    return (








    <div>
       <div className="d-flex justify-content-around flex-wrap">
        {this.state.users.map(users =>
        <Card style={{ width: '16rem',border:'none',marginBottom:'20px' }}>
           <Card.Img variant="top" src={users.posterUrl} height="300px" style={{borderRadius:'5px'}}/>

          <Card.Body>
      <Card.Title className="card-title">{users.title}</Card.Title>
      <Card.Text>

      </Card.Text>
      <div className="d-flex justify-content-between">
      <div className="card-text">{users.imdbRating}<i class="fas fa-star ml-1 rating"></i></div>



      </div>
      <div className="d-flex justify-content-center">
      <Rater  total={5} interactive={false} rating={users.imdbRating} />
      </div>
          </Card.Body>
          {/* button delete */}
        </Card>
        )}
  </div>
</div>
    )
}
}
export default Admine ;
