import React from 'react'

import {Card} from 'react-bootstrap';
import axios from 'axios';
import Rater from 'react-rater'
import 'react-rater/lib/react-rater.css'

import {Tab,Row,Col,Nav} from 'react-bootstrap';
import Post from './Post';
import Ubdat from './Ubdat';
import {useState,useEffect} from 'react';


function Admine ({Inputtt,handleChangee}) {
  const [movie, setMovie] = useState([])
  const getMovie = ()=>{

    axios.get('https://react-crud-ddca5-default-rtdb.firebaseio.com/posts.json').then((
          response) => {setMovie(response.data);} 

    ) 

  }

  useEffect(()=> {getMovie()
  },[])

   const delta = (id) => {
    axios.delete(`https://react-crud-ddca5-default-rtdb.firebaseio.com/posts.json${id}`).then((response) => {
      setMovie(response.data);
      console.log(response.data);
    });
  };



    return (



      <div>
            <Tab.Container id="left-tabs-example" defaultActiveKey="first">
  <Row>
    <Col sm={4}>
      <Nav variant="pills" className="flex-column">
        <Nav.Item>
          <Nav.Link eventKey="first">GetMovies</Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link eventKey="second">PostMovies</Nav.Link>
        </Nav.Item>
      </Nav>
    </Col>
    <Col sm={8}>
      <Tab.Content>
        <Tab.Pane eventKey="first">

        <div>
       <div className="d-flex justify-content-around flex-wrap">
        {movie.map(el =>
        <Card style={{ width: '16rem',border:'none',marginBottom:'20px' }}>
           <Card.Img variant="top" src={el.posterUrl} height="300px" style={{borderRadius:'5px'}}/>

          <Card.Body>
      <Card.Title className="card-title">{el.title}</Card.Title>
      <Card.Text>
      <div className="d-flex justify-content-around flex-wrap">
        <button onClick={() => delta(el.id)}>    <i class="fas fa-trash "></i>  </button>

        <Ubdat Inputtt={Inputtt} el={el} handleChangee={handleChangee}/>
          </div>
      </Card.Text>
      <div className="d-flex justify-content-between">
      <div className="card-text">{el.imdbRating}<i class="fas fa-star ml-1 rating"></i></div>



      </div>
      <div className="d-flex justify-content-center">
      <Rater  total={5} interactive={false} rating={el.imdbRating} />
      </div>
          </Card.Body>

          {/* button delete */}
        </Card>
        )}
  </div>
</div>


        </Tab.Pane>
        <Tab.Pane eventKey="second">
            <Post />
        </Tab.Pane>
      </Tab.Content>
    </Col>
  </Row>
</Tab.Container>
        </div>





    )
}

export default Admine ;
